import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

function App() {
  return (
    <Router>
      <div>
        <Routes>
          {/* Halaman Awal */}
          <Route path="/" element={<Home />} />
          {/* Halaman Daftar */}
          <Route path="/register" element={<Register />} />
          {/* Halaman Login */}
          <Route path="/login" element={<Login />} />
        </Routes>
      </div>
    </Router>
  );
}

// Komponen Halaman Awal
function Home() {
  return (
    <div>
      <h1>Selamat Datang di HoneyMoney</h1>
      <Link to="/register">
        <button>Daftar</button>
      </Link>
      <Link to="/login">
        <button>Login</button>
      </Link>
    </div>
  );
}

// Komponen Halaman Daftar
function Register() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirm, setConfirm] = useState('');
  const [message, setMessage] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    if (password !== confirm) {
      setMessage('Password dan konfirmasi password tidak sama!');
      return;
    }
    const response = await fetch('http://localhost:8000/register/', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password }),
    });
    const data = await response.json();
    if (response.ok) {
      setMessage('Registrasi berhasil!');
    } else {
      setMessage(data.username ? data.username[0] : 'Registrasi gagal!');
    }
  };

  return (
    <div>
      <h2>Register</h2>
      <form onSubmit={handleRegister}>
        <input
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Username"
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
        />
        <input
          type="password"
          value={confirm}
          onChange={(e) => setConfirm(e.target.value)}
          placeholder="Konfirmasi Password"
          required
        />
        <button type="submit">Register</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}


function Login() {
  return (
    <div>
      <h2>Login</h2>
      <p>Halaman login belum diimplementasikan.</p>
    </div>
  );
}

export default App;
